import defaults from './../app/defaults';

var moment = require('moment');
export default class {
    constructor(appManger) {
        this.appManger = appManger;
    }

    asFormattedString(date) {
        let format = defaults.DATE_TIME_FORMAT;
        if (this.appManager.settings.dateTimeFormat) {
            format = this.appManager.settings.dateTimeFormat;
        }
        return moment(date).format(format);
    }
}
