import BasePlugin from './../basePlugin';

let instance

class YoutubePlugin extends BasePlugin {

    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new YoutubePlugin('youtube', 'Youtube', 'Edit Youtube', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('youtube', {
            label  : 'Add Youtube',
            command: 'youtube',
            icon   : 'youtube'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'youtube';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('youtube', 1);
        editor.addMenuItems({
            youtube_deleteYoutube : {
                label  : 'Delete youtube video',
                group  : 'youtube',
                command: self.code + 'delete',
                order  : 1
            },
            youtube_props       : {
                label  : 'Youtube properties',
                group  : 'youtube',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('youtube') || element.$.closest('.youtube')) {
                return {
                    youtube               : CKEDITOR.TRISTATE_OFF,
                    youtube_deleteYoutube : CKEDITOR.TRISTATE_OFF,
                    youtube_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let youtube = element.$.closest('.youtube');
            let youtubeData = {};
            youtube.childNodes.forEach(element => {
                if (element.localName == 'app-youtube') {
                    youtubeData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('youtube', youtubeData);
            if (!newProps) {
                return;
            }

            while (youtube.firstChild) {
                youtube.removeChild(youtube.firstChild);
            }

            let item = editor.document.createElement('app-youtube');
            item.$.setAttribute('videourl', newProps.videourl);
            item.$.setAttribute('controls', newProps.controls);
            item.$.setAttribute('fs', newProps.fs);
            item.$.setAttribute('autoplay', newProps.autoplay);
            item.$.setAttribute('mute', newProps.mute);
            item.$.setAttribute('showinfo', newProps.showinfo);
            item.$.setAttribute('rel', newProps.rel);
            item.$.setAttribute('start', newProps.start);
            item.$.setAttribute('end', newProps.end);
            item.$.setAttribute('loop', newProps.loop);
            item.$.setAttribute('playlist', newProps.playlist);
            item.$.setAttribute('list', newProps.list);
            item.$.setAttribute('listtype', newProps.listtype);

            let date = new Date();
            let id = 'created_at_' + date.getTime();
            item.$.setAttribute('id', id);

            youtube.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let youtube = element.$.closest('.youtube');
                let confirmation = await self.confirm('Youtube video will be lost! Delete?');
                if (confirmation) {
                    while (youtube.firstChild) {
                        youtube.removeChild(youtube.firstChild);
                    }
                    youtube.parentElement.removeChild(youtube);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('youtube');

                    let newProps = await self.onShowDialogRequested('youtube', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-youtube');
                    item.$.setAttribute('videourl', newProps.videourl);
                    item.$.setAttribute('controls', newProps.controls);
                    item.$.setAttribute('fs', newProps.fs);
                    item.$.setAttribute('autoplay', newProps.autoplay);
                    item.$.setAttribute('mute', newProps.mute);
                    item.$.setAttribute('showinfo', newProps.showinfo);
                    item.$.setAttribute('rel', newProps.rel);
                    item.$.setAttribute('start', newProps.start);
                    item.$.setAttribute('end', newProps.end);
                    item.$.setAttribute('loop', newProps.loop);
                    item.$.setAttribute('playlist', newProps.playlist);
                    item.$.setAttribute('list', newProps.list);
                    item.$.setAttribute('listtype', newProps.listtype);

                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    item.$.setAttribute('id', id);

                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default YoutubePlugin;
