export default {
    build(Vue, options = {}) {
        let uri = 'auth';
        let customActions = {
            login : {method: 'POST', url: uri + '/login'},
            logout: {method: 'POST', url: uri + '/logout'},
        };
        if (options.uri) {
            uri = options.uri;
        }
        if (options.customActions) {
            customActions = Object.assign(customActions, options.customActions);
        }
        return Vue.resource(uri, {}, customActions);
    }
}
