import BasePlugin from './../basePlugin';

let instance;

class SoundcloudPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new SoundcloudPlugin('soundcloud', 'Soundcloud', 'Edit Soundcloud', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('soundcloud', {
            label  : 'Add Soundcloud',
            command: 'soundcloud',
            icon   : 'soundcloud'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'soundcloud';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('soundcloud', 1);
        editor.addMenuItems({
            soundcloud_deleteSoundcloud : {
                label  : 'Delete soundcloud',
                group  : 'soundcloud',
                command: self.code + 'delete',
                order  : 1
            },
            soundcloud_props            : {
                label  : 'Soundcloud properties',
                group  : 'soundcloud',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('soundcloud') || element.$.closest('.soundcloud')) {
                return {
                    soundcloud                  : CKEDITOR.TRISTATE_OFF,
                    soundcloud_deleteSoundcloud : CKEDITOR.TRISTATE_OFF,
                    soundcloud_props            : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let soundcloud = element.$.closest('.soundcloud');
            let soundcloudData = {};
            soundcloud.childNodes.forEach(element => {
                if (element.localName == 'app-soundcloud') {
                    soundcloudData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('soundcloud', soundcloudData);
            if (!newProps) {
                return;
            }

            while (soundcloud.firstChild) {
                soundcloud.removeChild(soundcloud.firstChild);
            }

            let item = editor.document.createElement('app-soundcloud');
            item.$.setAttribute('url', newProps.url);
            item.$.setAttribute('autoplay', newProps.autoplay);
            item.$.setAttribute('artwork', newProps.artwork);
            item.$.setAttribute('color', newProps.color);

            let date = new Date();
            let id = 'created_at_' + date.getTime();
            item.$.setAttribute('id', id);

            soundcloud.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let soundcloud = element.$.closest('.soundcloud');
                let confirmation = await self.confirm('Soundcloud will be lost! Delete?');
                if (confirmation) {
                    while (soundcloud.firstChild) {
                        soundcloud.removeChild(soundcloud.firstChild);
                    }
                    soundcloud.parentElement.removeChild(soundcloud);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('soundcloud');

                    let newProps = await self.onShowDialogRequested('soundcloud', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-soundcloud');
                    item.$.setAttribute('url', newProps.url);
                    item.$.setAttribute('autoplay', newProps.autoplay);
                    item.$.setAttribute('artwork', newProps.artwork);
                    item.$.setAttribute('color', newProps.color);
                    
                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    item.$.setAttribute('id', id);

                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default SoundcloudPlugin;
