export default {
    html:   `
    <template>
            <ContentEditor
                            v-model="editorValue"
                            :toolbar="fullToolbar"
                    >
                    </ContentEditor>
        </template>    
    `,
    script: `    
        <-script>
            export default {
                data() {
                    return {
                        editorValue: \`Hi! I'm visual html editor.\`,
                        fullToolbar: [
                            ['Undo', 'Redo'],
                            ['Bold', 'Italic', "Underline", 'Strike', "Bold","Subscript","Superscript","Blockquote","RemoveFormat"],
                            ["NumberedList","BulletedList"],
                            ["Outdent","Indent","JustifyLeft","JustifyCenter","JustifyRight","JustifyBlock"],
                            ["Link","Unlink","Anchor","InsertPre"],
                            ["Format","Font","FontSize"],
                            ["TextColor","BGColor"],
                            ['Cut', 'Copy'],
                            ['Table']
                        ],
                        wordsCount:  0
                    }
                },
                methods:    {
                    updateWordsCount() {
                        this.wordsCount = striptags(this.editorValue).split(' ').length;
                    }
                },
                mounted() {
                    this.updateWordsCount();
                },
                watch:      {
                    editorValue(newVal) {
                        this.updateWordsCount();
                    }
                }
            }
        <-/script> //remove the extra -`
}
