import BasePlugin from './../basePlugin';

let instance;

class ImgurPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new ImgurPlugin('imgur', 'Imgur', 'Edit Imgur Post', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('imgur', {
            label  : 'Add Imgur',
            command: 'imgur',
            icon   : 'imgur'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'imgur';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('imgur', 1);
        editor.addMenuItems({
            imgur_deleteImgur : {
                label  : 'Delete imgur',
                group  : 'imgur',
                command: self.code + 'delete',
                order  : 1
            },
            imgur_props       : {
                label  : 'Imgur properties',
                group  : 'imgur',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('imgur') || element.$.closest('.imgur')) {
                return {
                    imgur               : CKEDITOR.TRISTATE_OFF,
                    imgur_deleteImgur   : CKEDITOR.TRISTATE_OFF,
                    imgur_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let imgur = element.$.closest('.imgur');
            let imgurData = {};
            imgur.childNodes.forEach(element => {
                if (element.localName == 'app-imgur') {
                    imgurData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('imgur', imgurData);
            if (!newProps) {
                return;
            }

            while (imgur.firstChild) {
                imgur.removeChild(imgur.firstChild);
            }

            let item = editor.document.createElement('app-imgur');
            item.$.setAttribute('id', newProps.id);
            item.$.setAttribute('responsive', newProps.responsive);
            item.$.setAttribute('height', newProps.height);
            item.$.setAttribute('width', newProps.width);
            imgur.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(newProps.id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        }

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let imgur = element.$.closest('.imgur');
                let confirmation = await self.confirm('Imgur will be lost! Delete?');
                if (confirmation) {
                    while (imgur.firstChild) {
                        imgur.removeChild(imgur.firstChild);
                    }
                    imgur.parentElement.removeChild(imgur);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('imgur');

                    let newProps = await self.onShowDialogRequested('imgur', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-imgur');
                    item.$.setAttribute('id', newProps.id);
                    item.$.setAttribute('responsive', newProps.responsive);
                    item.$.setAttribute('height', newProps.height);
                    item.$.setAttribute('width', newProps.width);
                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(newProps.id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default ImgurPlugin;
