class LocalSettings {
    constructor() {
        this.settings = localStorage.getItem('bi_admin_panel_settings');
        if (!this.settings) {
            this._initSettings();
        }
        try {
            this.settings = JSON.parse(this.settings);
        } catch (e) {
            this._initSettings();
        }
    }

    _initSettings() {
        this.settings = {};
        localStorage.setItem('bi_admin_panel_settings', JSON.stringify(this.settings));
    }

    getAll() {
        return this.settings;
    }

    get(name) {
        if (this.settings[name] === undefined) {
            return null;
        }
        return this.settings[name];
    }

    set(name, value) {
        this.settings[name] = value;
        localStorage.setItem('bi_admin_panel_settings', JSON.stringify(this.settings));
        return this.settings;
    }
}

export default new LocalSettings();
