export default {
    html:   `
    <template>
            <ContentEditor
                            v-model="editorValue"
                    >
                    </ContentEditor>
        </template>    
    `,
    script: `    
        <-script>
            export default {
                data() {
                    return {
                        editorValue: \`Hi! I'm visual html editor.\`,                        
                        wordsCount:  0
                    }
                },
                methods:    {
                    updateWordsCount() {
                        this.wordsCount = striptags(this.editorValue).split(' ').length;
                    }
                },
                mounted() {
                    this.updateWordsCount();
                },
                watch:      {
                    editorValue(newVal) {
                        this.updateWordsCount();
                    }
                }
            }
        <-/script> //remove the extra -`
}
