import BasePlugin from './../basePlugin';

let instance;

class PicturePlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new PicturePlugin('picture', 'Picture', 'Edit Picture', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires: 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('picture', {
            label  : 'Add Picture',
            command: 'picture',
            icon   : 'picture'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'picture';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('picture', 1);
        editor.addMenuItems({
            picture_deletePicture  : {
                label  : 'Delete picture',
                group  : 'picture',
                command: self.code + 'delete',
                order  : 1
            },
            picture_props       : {
                label  : 'Picture properties',
                group  : 'picture',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('picture') || element.$.closest('.picture')) {
                return {
                    picture                 : CKEDITOR.TRISTATE_OFF,
                    picture_deletePicture   : CKEDITOR.TRISTATE_OFF,
                    picture_props           : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let currentItem = element.$.closest('.picture');
            let currentData = {};
            currentItem.childNodes.forEach(element => {
                if (element.localName == 'app-picture') {
                    currentData = element;
                }
            });

            let images = [];
            let image = {};
            for (var i = 0; i < currentData.attributes.length; i++) {
                let name = currentData.attributes.item(i).name;
                let value = currentData.attributes.item(i).value;

                if (name == 'thumb-sizes') {
                    image.thumbSizes = [];
                    value.split(',').forEach(element => {
                        image.thumbSizes.push(Number(element));
                    });
                }
                else if (name == 'thumb-formats') {
                    image.thumbFormats = value.split(',');
                } else if (name != 'vce-ready') {
                    image[name] = value;
                }
            }
            images.push(image);

            let newProps = await self.onShowDialogRequested({
                images: images,
                type: 'picture'
            });
            if (!newProps) {
                return;
            }

            while (element.$.firstChild) {
                element.$.removeChild(element.$.firstChild);
            }

            let item = editor.document.createElement('app-picture');
            item.setAttribute('code', newProps.images[0].code);
            item.setAttribute('thumb-sizes', newProps.images[0].thumbSizes);
            item.setAttribute('thumb-formats', newProps.images[0].thumbFormats);
            item.setAttribute('title', newProps.images[0].title);
            item.setAttribute('description', newProps.images[0].description);
            item.setAttribute('alt', newProps.images[0].alt);
            item.setAttribute('lightbox', newProps.images[0].lightbox);
            currentItem.appendChild(item.$);
            editor.fire('change');
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let picture = element.$.closest('.picture');
                let confirmation = await self.confirm('Picture will be lost! Delete?');
                if (confirmation) {
                    while (picture.firstChild) {
                        picture.removeChild(picture.firstChild);
                    }
                    picture.parentElement.removeChild(picture);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('picture');

                    let newProps = await self.onShowDialogRequested({ type: 'picture' });
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-picture');
                    item.setAttribute('code', newProps.images[0].code);
                    item.setAttribute('thumb-sizes', newProps.images[0].thumbSizes);
                    item.setAttribute('thumb-formats', newProps.images[0].thumbFormats);
                    item.setAttribute('title', newProps.images[0].title);
                    item.setAttribute('description', newProps.images[0].description);
                    item.setAttribute('alt', newProps.images[0].alt);
                    item.setAttribute('lightbox', newProps.images[0].lightbox);

                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    item.$.setAttribute('id', id);

                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default PicturePlugin;
