import BasePlugin from './../basePlugin';

let instance;

class MixerPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new MixerPlugin('mixer', 'Mixer', 'Edit Mixer Post', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init: function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('mixer', {
            label  : 'Add Mixer',
            command: 'mixer',
            icon   : 'mixer'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'mixer';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('mixer', 1);
        editor.addMenuItems({
            mixer_deleteMixer  : {
                label  : 'Delete mixer',
                group  : 'mixer',
                command: self.code + 'delete',
                order  : 1
            },
            mixer_props       : {
                label  : 'Mixer properties',
                group  : 'mixer',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('mixer') || element.$.closest('.mixer')) {
                return {
                    mixer               : CKEDITOR.TRISTATE_OFF,
                    mixer_deleteMixer   : CKEDITOR.TRISTATE_OFF,
                    mixer_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        editor.addCommand(this.code, new CKEDITOR.dialogCommand(this.code));
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let mixer = element.$.closest('.mixer');
            let mixerData = {};
            mixer.childNodes.forEach(element => {
                if (element.localName == 'app-mixer') {
                    mixerData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('mixer', mixerData);
            if (!newProps) {
                return;
            }
            while (mixer.firstChild) {
                mixer.removeChild(mixer.firstChild);
            }
            let item = editor.document.createElement('app-mixer');
            item.$.setAttribute('id', newProps.id);
            item.$.setAttribute('type', newProps.type);
            item.$.setAttribute('hidechannel', newProps.hidechannel);
            item.$.setAttribute('muted', newProps.muted);
            item.$.setAttribute('disablecostream', newProps.disablecostream);
            item.$.setAttribute('disablelowlatency', newProps.disablelowlatency);
            mixer.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(newProps.id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let mixer = element.$.closest('.mixer');
                let confirmation = await self.confirm('Mixer will be lost! Delete?');
                if (confirmation) {
                    while (mixer.firstChild) {
                        mixer.removeChild(mixer.firstChild);
                    }
                    mixer.parentElement.removeChild(mixer);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('mixer');

                    let newProps = await self.onShowDialogRequested('mixer', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-mixer');
                    item.$.setAttribute('id', newProps.id);
                    item.$.setAttribute('type', newProps.type);
                    item.$.setAttribute('hidechannel', newProps.hidechannel);
                    item.$.setAttribute('muted', newProps.muted);
                    item.$.setAttribute('disablecostream', newProps.disablecostream);
                    item.$.setAttribute('disablelowlatency', newProps.disablelowlatency);
                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);
                
                    let newElement = document.getElementById(newProps.id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default MixerPlugin;
