export default class {
    static unwrapElement(element) {
        let parent = el.parentNode;
        while (element.firstChild) parent.insertBefore(element.firstChild, element);
        parent.removeChild(element);
    }
    static deleteDivWithClass(className) {
        let overlayDivs = document.querySelectorAll('.' + className);
        for (let i = 0; i < overlayDivs.length; i++) {
            overlayDivs[i].parentNode.removeChild(overlayDivs[i]);
        }
    }
    static showDivWithClass(className, show) {
        let overlayDivs = document.querySelectorAll('.' + className);
        if (overlayDivs) {
            for (let i = 0; i < overlayDivs.length; i++) {
                overlayDivs[i].style.display = show ? 'block' : 'none';
            }

        }
    }
}
