import BasePlugin from './../basePlugin';

let instance;

class TwitchPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new TwitchPlugin('twitch', 'Twitch', 'Edit Twitch Video', {
                element: "div",
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu',
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('twitch', {
            label  : 'Add Twitch',
            command: 'twitch',
            icon   : 'twitch'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'twitch';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('twitch', 1);
        editor.addMenuItems({
            twitch_deleteTwitch  : {
                label  : 'Delete twitch',
                group  : 'twitch',
                command: self.code + 'delete',
                order  : 1
            },
            twitch_props       : {
                label  : 'Twitch properties',
                group  : 'twitch',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('twitch') || element.$.closest('.twitch')) {
                return {
                    twitch               : CKEDITOR.TRISTATE_OFF,
                    twitch_deleteTwitch  : CKEDITOR.TRISTATE_OFF,
                    twitch_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        editor.addCommand(this.code, new CKEDITOR.dialogCommand(this.code));
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let twitch = element.$.closest('.twitch');
            let twitchData = {};
            twitch.childNodes.forEach(element => {
                if (element.localName == 'app-twitch') {
                    twitchData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('twitch', twitchData);
            if (!newProps) {
                return;
            }

            while (twitch.firstChild) {
                twitch.removeChild(twitch.firstChild);
            }

            let item = editor.document.createElement('app-twitch');
            item.$.setAttribute('src', newProps.src);
            item.$.setAttribute('srctype', newProps.srctype);
            item.$.setAttribute('autoplay', newProps.autoplay);
            item.$.setAttribute('muted', newProps.muted);
            item.$.setAttribute('time', newProps.time);

            let date = new Date();
            let id = 'created_at_' + date.getTime();
            item.$.setAttribute('id', id);

            twitch.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let twitch = element.$.closest('.twitch');
                let confirmation = await self.confirm('Twitch video will be lost! Delete?');
                if (confirmation) {
                    while (twitch.firstChild) {
                        twitch.removeChild(twitch.firstChild);
                    }
                    twitch.parentElement.removeChild(twitch);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('twitch');

                    let newProps = await self.onShowDialogRequested('twitch', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-twitch');
                    item.$.setAttribute('src', newProps.src);
                    item.$.setAttribute('srctype', newProps.srctype);
                    item.$.setAttribute('autoplay', newProps.autoplay);
                    item.$.setAttribute('muted', newProps.muted);
                    item.$.setAttribute('time', newProps.time);

                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    item.$.setAttribute('id', id);

                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default TwitchPlugin;
