import BasePlugin from './../basePlugin';

let instance;

class GridPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new GridPlugin('grid', 'Grid', 'Edit Grid', {
                element: "div",
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init     : function (editor) {
                    instance.init(editor);
                },
                editables: {
                    items: {
                        selector      : '.grid-item',
                        allowedContent: true
                    },
                },
                requires : 'contextmenu',
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('layout', {
            label  : 'Add Layout',
            command: 'layout',
            icon   : 'layout'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'grid';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('grid', 3);
        editor.addMenuGroup('grid_item', 4);
        editor.addMenuItems({
            grid             : {
                label   : 'insert cell',
                group   : 'grid',
                order   : 21,
                //command: self.code+'insertbefore'
                getItems: function () {
                    return {
                        grid_insertBefore: CKEDITOR.TRISTATE_OFF,
                        grid_insertAfter : CKEDITOR.TRISTATE_OFF,
                    }
                }
            },
            grid_insertBefore: {
                label  : 'Insert before',
                group  : 'grid_item',
                command: self.code + 'insertbefore',
                order  : 22
            },
            grid_insertAfter : {
                label  : 'Insert after',
                group  : 'grid_item',
                command: self.code + 'insertafter',
                order  : 23
            },
            grid_deleteCell  : {
                label  : 'Delete cell',
                group  : 'grid_item',
                command: self.code + 'deleteitem',
                order  : 24
            },
            grid_deleteGrid  : {
                label  : 'Delete grid',
                group  : 'grid_item',
                command: self.code + 'delete',
                order  : 25
            },
            grid_cellProps   : {
                label  : 'Cell properties',
                group  : 'grid_item',
                command: self.code + 'cellprops',
                order  : 26
            },
            grid_props       : {
                label  : 'Grid properties',
                group  : 'grid_item',
                command: self.code + 'props',
                order  : 26
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && (element.$.classList.contains('grid-item') || element.$.classList.contains('grid')) ||
                element.$.closest('.grid-item') || element.$.closest('.grid')) {
                return {
                    grid           : CKEDITOR.TRISTATE_OFF,
                    grid_deleteCell: CKEDITOR.TRISTATE_OFF,
                    grid_deleteGrid: CKEDITOR.TRISTATE_OFF,
                    grid_cellProps : CKEDITOR.TRISTATE_OFF,
                    grid_props     : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;
        editor.addCommand('layout', {
            exec: async function (editor) {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('grid');
                    self.insertMode = true;
                } else {
                    self.insertMode = false;
                }

                let layoutData = await self.onShowLayoutDialogRequested();
                if (!layoutData) {
                    return;
                }
                element.$.classList.add(`grid-horizontal`);
                if (layoutData.type === 0) { //t+t
                    let item1 = editor.document.createElement('div');
                    item1.$.classList.add('grid-item');
                    item1.$.classList.add('grid-item-grow');
                    item1.$.innerHTML = 'Lorem ipsum 1';
                    element.$.appendChild(item1.$);
                    let item2 = editor.document.createElement('div');
                    item2.$.classList.add('grid-item');
                    item2.$.classList.add('grid-item-grow');
                    item2.$.innerHTML = 'Lorem ipsum 2';
                    element.$.appendChild(item2.$);
                } else if (layoutData.type === 1) { //i+t
                    let item1 = editor.document.createElement('div');
                    item1.$.classList.add('grid-item');
                    item1.$.innerHTML = '<img src="data:image/png;base64,\n">';
                    element.$.appendChild(item1.$);
                    let item2 = editor.document.createElement('div');
                    item2.$.classList.add('grid-item');
                    item2.$.classList.add('grid-item-grow');
                    item2.$.innerHTML = 'Lorem ipsum';
                    element.$.appendChild(item2.$);
                } else if (layoutData.type === 2) { //i+t
                    let item1 = editor.document.createElement('div');
                    item1.$.classList.add('grid-item');
                    item1.$.classList.add('grid-item-grow');
                    item1.$.innerHTML = 'Lorem ipsum';
                    element.$.appendChild(item1.$);
                    let item2 = editor.document.createElement('div');
                    item2.$.classList.add('grid-item');
                    item2.$.innerHTML = '<img src="data:image/png;base64,\n">';
                    element.$.appendChild(item2.$);
                } else if (layoutData.type === 3) { //i+i
                    let item1 = editor.document.createElement('div');
                    item1.$.classList.add('grid-item');
                    item1.$.innerHTML = '&nbsp;<img src="data:image/png;base64,\n">';
                    element.$.appendChild(item1.$);
                    let item2 = editor.document.createElement('div');
                    item2.$.classList.add('grid-item');
                    item2.$.innerHTML = '&nbsp;<img src="data:image/png;base64,\n">';
                    element.$.appendChild(item2.$);
                }
                editor.insertElement(element);
                editor.widgets.initOn(element, self.code);
            }
        });
        editor.addCommand(this.code + 'props', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                let grid = element.$.closest('.grid');
                let newProps = await self.onShowDialogRequested({
                    direction: grid.classList.contains('grid-horizontal') ? 'row' : 'column'
                });
                if (!newProps) {
                    return;
                }
                if (newProps.direction === 'row') {
                    grid.classList.add('grid-horizontal');
                    grid.classList.remove('grid-vertical');
                } else {
                    grid.classList.add('grid-vertical');
                    grid.classList.remove('grid-horizontal');
                }
            }
        });
        editor.addCommand(this.code + 'cellprops', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                let gridItem = element.$.closest('.grid-item');
                let newProps = await self.onShowGridItemDialogRequested({
                    grow: gridItem.classList.contains('grid-item-grow')
                });
                if (!newProps) {
                    return;
                }
                if (newProps.grow) {
                    gridItem.classList.add('grid-item-grow');
                } else {
                    gridItem.classList.remove('grid-item-grow');
                }
            }
        }),
            editor.addCommand(this.code + 'deleteitem', {
                exec: async function (editor) {
                    let selection = editor.getSelection();
                    let element = selection.getStartElement();

                    let gridItem = element.$.closest('.grid-item');
                    let confirmation = await self.confirm('All cell-item content will be lost! Delete?');
                    if (confirmation) {
                        gridItem.parentElement.removeChild(gridItem);
                        editor.fire('change');
                    }
                }
            });
        editor.addCommand(this.code + 'delete', {
            exec: async function (editor) {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let grid = element.$.closest('.grid');
                let confirmation = await self.confirm('All table content will be lost! Delete?');
                if (confirmation) {
                    grid.parentElement.removeChild(grid);
                    editor.fire('change');
                }
            }
        });
        editor.addCommand(this.code + 'insertbefore', {
            exec: async function (editor) {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                let gridItem = element.$.closest('.grid-item');

                let newGridItem = editor.document.createElement('div');
                newGridItem.$.classList.add('grid-item');
                newGridItem.$.innerHTML = 'New item';

                gridItem.parentNode.insertBefore(newGridItem.$, gridItem);
                editor.fire('change');
            }
        });
        editor.addCommand(this.code + 'insertafter', {
            exec: async function (editor) {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                let gridItem = element.$.closest('.grid-item');

                let newGridItem = editor.document.createElement('div');
                newGridItem.$.classList.add('grid-item');
                newGridItem.$.innerHTML = 'New item';

                gridItem.parentNode.insertBefore(newGridItem.$, gridItem.nextSibling);
            }
        });
        editor.addCommand(this.code, {
            exec: async function (editor) {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('grid');
                    element.$.classList.add('layout');
                    self.insertMode = true;
                } else {
                    self.insertMode = false;
                }

                let gridData = {
                    //TODO: parse element
                };

                let newGridData = await self.onShowDialogRequested(gridData);
                if (!newGridData) {
                    return;
                }
                if (self.insertMode) {
                    let item = editor.document.createElement('div');
                    element.$.classList.add(`grid-${newGridData.direction === 'row' ? 'horizontal' : 'vertical'}`);
                    item.$.classList.add('grid-item');
                    item.$.innerHTML = 'Item-1';
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);
                } else {
                    //apply changes
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
        this.insertMode = false;
    }
}

export default GridPlugin;
