import BasePlugin from './../basePlugin';

let instance;

class VimeoPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new VimeoPlugin('vimeo', 'Vimeo', 'Edit Vimeo Video', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('vimeo', {
            label  : 'Add Vimeo Video',
            command: 'vimeo',
            icon   : 'vimeo'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'vimeo';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('vimeo', 1);
        editor.addMenuItems({
            vimeo_deleteVimeo  : {
                label  : 'Delete vimeo video',
                group  : 'vimeo',
                command: self.code + 'delete',
                order  : 1
            },
            vimeo_props       : {
                label  : 'Vimeo video properties',
                group  : 'vimeo',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('vimeo') || element.$.closest('.vimeo')) {
                return {
                    vimeo               : CKEDITOR.TRISTATE_OFF,
                    vimeo_deleteVimeo   : CKEDITOR.TRISTATE_OFF,
                    vimeo_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let vimeo = element.$.closest('.vimeo');
            let vimeoData = {};
            vimeo.childNodes.forEach(element => {
                if (element.localName == 'app-vimeo') {
                    vimeoData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('vimeo', vimeoData);
            if (!newProps) {
                return;
            }

            while (vimeo.firstChild) {
                vimeo.removeChild(vimeo.firstChild);
            }

            let item = editor.document.createElement('app-vimeo');
            item.$.setAttribute('id', newProps.id);
            item.$.setAttribute('loop', newProps.loop);
            item.$.setAttribute('title', newProps.title);
            item.$.setAttribute('fullscreen', newProps.fullscreen);
            vimeo.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(newProps.id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let vimeo = element.$.closest('.vimeo');
                let confirmation = await self.confirm('Vimeo video will be lost! Delete?');
                if (confirmation) {
                    while (vimeo.firstChild) {
                        vimeo.removeChild(vimeo.firstChild);
                    }
                    vimeo.parentElement.removeChild(vimeo);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('vimeo');

                    let newProps = await self.onShowDialogRequested('vimeo', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-vimeo');
                    item.$.setAttribute('id', newProps.id);
                    item.$.setAttribute('loop', newProps.loop);
                    item.$.setAttribute('title', newProps.title);
                    item.$.setAttribute('fullscreen', newProps.fullscreen);
                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(newProps.id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default VimeoPlugin;
