export default {
    html:   `
    <template>
        Number of words: {{wordsCount}}
        <Editor
                v-model="editorValue"
                :source="true"
                :sourceOptions="sourceOptions"
        >
        </Editor>
    </template>    
    `,
    script: `    
        <-script> //remove the extra -
            import Editor from '../../components/editor/editor'            
        
            var striptags = require('striptags');
        
            export default {
                components: {
                    Editor,
                },                
                data() {
                    return {
                        editorValue  : \`<div><p>hello this is paragraph 1</p><p>hello this is paragraph 2</p></div>\`,
                        wordsCount   : 0,
                        sourceOptions: {
                            formatAfterMount: true,
                            height          : 600,
                            language        : 'html'
                        }
                    }
                },
                methods   : {
                    updateWordsCount() {
                        this.wordsCount = striptags(this.editorValue).split(' ').length;
                    }
                },
                mounted() {
                    this.updateWordsCount();
                },
                watch     : {
                    editorValue(newVal) {
                        this.updateWordsCount();
                    }
                }
            }  
        <-/script> //remove the extra -`
}
