import BasePlugin from './../basePlugin';

let instance;

class FacebookPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new FacebookPlugin('facebook', 'Facebook', 'Edit Facebook', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init    : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('facebook', {
            label  : 'Add Facebook',
            command: 'facebook',
            icon   : 'facebook'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'facebook';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('facebook', 1);
        editor.addMenuItems({
            facebook_deleteFacebook : {
                label  : 'Delete facebook',
                group  : 'facebook',
                command: self.code + 'delete',
                order  : 1
            },
            facebook_props          : {
                label  : 'Facebook properties',
                group  : 'facebook',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('facebook') || element.$.closest('.facebook')) {
                return {
                    facebook               : CKEDITOR.TRISTATE_OFF,
                    facebook_deleteFacebook: CKEDITOR.TRISTATE_OFF,
                    facebook_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
        // editor.on('doubleclick', function(e) {
        //     if (self.code == 'facebook') {
        //         self.editMethod(editor, self);
        //     }
        // });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();
        
            let facebook = element.$.closest('.facebook');
            let facebookData = {};
            facebook.childNodes.forEach(element => {
                if (element.localName == 'app-facebook') {
                    facebookData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('facebook', facebookData);
            if (!newProps) {
                return;
            }
        
            while (facebook.firstChild) {
                facebook.removeChild(facebook.firstChild);
            }
        
            let item = editor.document.createElement('app-facebook');
            item.$.setAttribute('url', newProps.url);
            item.$.setAttribute('type', newProps.type);
            item.$.setAttribute('tabs', newProps.tabs);
            item.$.setAttribute('responsive', newProps.responsive);
            item.$.setAttribute('height', newProps.height);
            item.$.setAttribute('width', newProps.width);

            let date = new Date();
            let id = 'created_at_' + date.getTime();
            item.$.setAttribute('id', id);

            facebook.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let facebook = element.$.closest('.facebook');
                let confirmation = await self.confirm('Facebook will be lost! Delete?');
                if (confirmation) {
                    while (facebook.firstChild) {
                        facebook.removeChild(facebook.firstChild);
                    }
                    facebook.parentElement.removeChild(facebook);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('facebook');

                    let newProps = await self.onShowDialogRequested('facebook', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-facebook');
                    item.$.setAttribute('url', newProps.url);
                    item.$.setAttribute('type', newProps.type);
                    item.$.setAttribute('tabs', newProps.tabs);
                    item.$.setAttribute('responsive', newProps.responsive);
                    item.$.setAttribute('height', newProps.height);
                    item.$.setAttribute('width', newProps.width);

                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    item.$.setAttribute('id', id);

                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default FacebookPlugin;
