import BasePlugin from './../basePlugin';

let instance;

class ComparsionPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new ComparsionPlugin('comparsion', 'Comparsion', 'Edit Comparsion', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init     : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('comparsion', {
            label  : 'Add Comparsion',
            command: 'comparsion',
            icon   : 'comparsion'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'comparsion';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('comparsion', 1);
        editor.addMenuItems({
            comparsion_deleteComparsion  : {
                label  : 'Delete comparsion',
                group  : 'comparsion',
                command: self.code + 'delete',
                order  : 1
            },
            comparsion_props       : {
                label  : 'Comparsion properties',
                group  : 'comparsion',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('comparsion') || element.$.closest('.comparsion')) {
                return {
                    comparsion                 : CKEDITOR.TRISTATE_OFF,
                    comparsion_deleteComparsion: CKEDITOR.TRISTATE_OFF,
                    comparsion_props           : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let currentItem = element.$.closest('.comparsion');
            let currentData = {};
            currentItem.childNodes.forEach(element => {
                if (element.localName == 'app-comparsion') {
                    currentData = element;
                }
            });

            let images = [];
            currentData.childNodes.forEach(slide => {
                if (slide.localName == 'app-gallery-slide') {
                    slide.childNodes.forEach(picture => {
                        if (picture.localName == 'app-picture') {
                            let image = {};
                            for (var i = 0; i < picture.attributes.length; i++) {
                                let name = picture.attributes.item(i).name;
                                let value = picture.attributes.item(i).value;

                                if (name == 'thumb-sizes') {
                                    image.thumbSizes = [];
                                    value.split(',').forEach(element => {
                                        image.thumbSizes.push(Number(element));
                                    });
                                }
                                else if (name == 'thumb-formats') {
                                    image.thumbFormats = value.split(',');
                                } else if (name != 'vce-ready') {
                                    image[name] = value;
                                }
                            }
                            images.push(image);
                        }
                    });
                }
            });

            let newProps = await self.onShowDialogRequested({ 
                    title: currentData.title,
                    description: currentData.description,
                    padding: currentData.padding,
                    carousel: currentData.carousel,
                    images: images,
                    type: 'comparsion'
                }
            );
            if (!newProps) {
                return;
            }

            while (element.$.firstChild) {
                element.$.removeChild(element.$.firstChild);
            }

            let comparsion = editor.document.createElement('app-comparsion');
            comparsion.setAttribute('title', newProps.title ? newProps.title : '');
            comparsion.setAttribute('description', newProps.description ? newProps.description : '');

            for (let i = 0; i < newProps.images.length; i++) {
                let slide = editor.document.createElement('app-gallery-slide');

                let picture = editor.document.createElement('app-picture');
                picture.setAttribute('code', newProps.images[i].code);
                picture.setAttribute('thumb-sizes', newProps.images[i].thumbSizes);
                picture.setAttribute('thumb-formats', newProps.images[i].thumbFormats);
                picture.setAttribute('title', newProps.images[i].title);
                picture.setAttribute('description', newProps.images[i].description);
                picture.setAttribute('alt', newProps.images[i].alt);
                picture.setAttribute('lightbox', newProps.images[i].lightbox);
                
                slide.append(picture);
                comparsion.append(slide);
            }

            currentItem.appendChild(comparsion.$);
            editor.fire('change');
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let comparsion = element.$.closest('.comparsion');
                let confirmation = await self.confirm('Comparsion will be lost! Delete?');
                if (confirmation) {
                    while (comparsion.firstChild) {
                        comparsion.removeChild(comparsion.firstChild);
                    }
                    comparsion.parentElement.removeChild(comparsion);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('comparsion');

                    let newProps = await self.onShowDialogRequested({ type: 'comparsion' });
                    if (!newProps) {
                        return;
                    }

                    let comparsion = editor.document.createElement('app-comparsion');
                    comparsion.setAttribute('title', newProps.title ? newProps.title : '');
                    comparsion.setAttribute('description', newProps.description ? newProps.description : '');

                    let date = new Date();
                    let id = 'created_at_' + date.getTime();
                    comparsion.setAttribute('id', id);

                    for (let i = 0; i < newProps.images.length; i++) {
                        let slide = editor.document.createElement('app-gallery-slide');

                        let picture = editor.document.createElement('app-picture');
                        picture.setAttribute('code', newProps.images[i].code);
                        picture.setAttribute('thumb-sizes', newProps.images[i].thumbSizes);
                        picture.setAttribute('thumb-formats', newProps.images[i].thumbFormats);
                        picture.setAttribute('title', newProps.images[i].title);
                        picture.setAttribute('description', newProps.images[i].description);
                        picture.setAttribute('alt', newProps.images[i].alt);
                        picture.setAttribute('lightbox', newProps.images[i].lightbox);

                        slide.append(picture);
                        comparsion.append(slide);
                    }

                    element.$.innerHTML = null;
                    element.$.appendChild(comparsion.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default ComparsionPlugin;
