import debug from 'debug';
import Router from 'vue-router';

import ParentPage from './../components/application/ParentPage';

const constructRouteObject = function(options, parent) {
    let route = {
        path     : options.path,
        name     : options.name,
        component: options.component || ParentPage,
        props    : {
            ...options
        },
    };

    if (!options.title) {
        route.props.title = options.name;
    }

    if (parent) {
        route.props.icon = options.icon || parent.props.icon;
    }

    if (options.children) {

        route.children = [];

        options.children.forEach(childRoute => {
            route.children.push(constructRouteObject(childRoute, route));
        });
    }

    return route;
}

const log = debug('bi-admin-vue:router');

export default {
    build: function(appManager, Vue) {
        Vue.use(Router);
        let routes = [];

        Object.keys(appManager.pages).map((pageCode) => {
            routes.push(constructRouteObject(appManager.pages[pageCode]));
        });

        routes.push({
            path    : '*',
            redirect: '/'
        });

        log('Registering routes: ', routes);

        let router = new Router({
            base  : window.basePath || '/',
            mode  : 'history',
            routes: routes
        });

        return router;
    }
};
