import BasePlugin from './../basePlugin';
import Tweet from './tweet';
import Vue from 'vue';

let instance;

class TweetPlugin extends BasePlugin {
    static createAndRegister(CKEDITOR) {
        if (!instance) {
            instance = new TweetPlugin('tweet', 'Tweet', 'Edit Tweet', {
                element: "div"
            });
        }
        if (!CKEDITOR.plugins.registered[instance.code]) {
            CKEDITOR.plugins.add(instance.code, {
                init     : function (editor) {
                    instance.init(editor);
                },
                requires : 'contextmenu'
            });
        }
        return instance;
    }

    registerButton(editor) {
        super.registerButton(editor);
        editor.ui.addButton('tweet', {
            label  : 'Add Tweet',
            command: 'tweet',
            icon   : 'tweet'
        });
    }

    registerWidget(editor) {
        let self = this;
        editor.widgets.add(this.ckOptions.element, {
            allowedContent: true,
            upcast        : function (element) {
                return element.className === 'tweet';
            },
            dialog: this.code
        });
    }

    registerContextMenu(editor) {
        let self = this;
        editor.addMenuGroup('tweet', 1);
        editor.addMenuItems({
            tweet_deleteTweet  : {
                label  : 'Delete tweet',
                group  : 'tweet',
                command: self.code + 'delete',
                order  : 1
            },
            tweet_props       : {
                label  : 'Tweet properties',
                group  : 'tweet',
                command: self.code + 'props',
                order  : 2
            }
        });
        editor.contextMenu.addListener(function (element, selection, path) {
            if (element.$.classList && element.$.classList.contains('tweet') || element.$.closest('.tweet')) {
                return {
                    tweet               : CKEDITOR.TRISTATE_OFF,
                    tweet_deleteTweet   : CKEDITOR.TRISTATE_OFF,
                    tweet_props         : CKEDITOR.TRISTATE_OFF
                };
            }
            return null;
        });
    }

    registerCommand(editor) {
        let self = this;

        self.editMethod = async function (editor, self) {
            let selection = editor.getSelection();
            let element = selection.getStartElement();

            let tweet = element.$.closest('.tweet');
            let tweetData = {};
            tweet.childNodes.forEach(element => {
                if (element.localName == 'app-tweet') {
                    tweetData = element;
                }
            });
            let newProps = await self.onShowDialogRequested('tweet', tweetData);
            if (!newProps) {
                return;
            }

            while (tweet.firstChild) {
                tweet.removeChild(tweet.firstChild);
            }

            let item = editor.document.createElement('app-tweet');
            item.$.setAttribute('id', newProps.id);
            item.$.setAttribute('showcard', newProps.showcard);
            item.$.setAttribute('darktheme', newProps.darktheme);
            tweet.appendChild(item.$);
            editor.fire('change');

            let newElement = document.getElementById(newProps.id);
            newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
        };

        editor.addCommand(this.code + 'props', {
            exec: () => { self.editMethod(editor, self); }
        });
        editor.addCommand(this.code + 'delete', {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();
                let tweet = element.$.closest('.tweet');
                let confirmation = await self.confirm('Tweet will be lost! Delete?');
                if (confirmation) {
                    while (tweet.firstChild) {
                        tweet.removeChild(tweet.firstChild);
                    }
                    tweet.parentElement.removeChild(tweet);
                }
                editor.fire('change');
            }
        });
        editor.addCommand(this.code, {
            exec: async function () {
                let selection = editor.getSelection();
                let element = selection.getStartElement();

                if (element) {
                    element = element.getAscendant(self.ckOptions.element, true);
                }

                if (!element || element.getName() !== self.ckOptions.element || element) {
                    element = editor.document.createElement('div');
                    element.$.classList.add('tweet');

                    let newProps = await self.onShowDialogRequested('tweet', {});
                    if (!newProps) {
                        return;
                    }

                    let item = editor.document.createElement('app-tweet');
                    item.$.setAttribute('id', newProps.id);
                    item.$.setAttribute('showcard', newProps.showcard);
                    item.$.setAttribute('darktheme', newProps.darktheme);
                    element.$.innerHTML = null;
                    element.$.appendChild(item.$);
                    editor.insertHtml(element.$.outerHTML);
                    editor.widgets.initOn(element, self.code);

                    let newElement = document.getElementById(newProps.id);
                    newElement.addEventListener('dblclick', function(event) { self.editMethod(editor, self); });
                }
            }
        });
    }

    constructor(code, buttonLabel, contextMenuEditLabel, ckOptions) {
        super(code, buttonLabel, contextMenuEditLabel, ckOptions);
    }
}

export default TweetPlugin;
