import {Validator} from 'vee-validate';

const validator = new Validator();
//validator.attach('email', 'email'); // attach field.

export default {
    getMessage: field => `${field} is not valid JSON`,
    validate  : value => {
        try {
            JSON.parse(value);
            return {
                valid: true
            };
        } catch (e) {
            return {
                valid: false
            };
        }
    }
};
