import SimpleTableFilterDropdownFilter from './../../components/table/filter/SimpleTableFilterDropdownFilter';
import SimpleTableFilterTextFilter from './../../components/table/filter/SimpleTableFilterTextFilter';
import TableController from './../../table/controller';
import SimpleTableSortOrder from './../../table/sortOrder';

export default {
    components: {
        SimpleTableFilterTextFilter
    },
    computed  : {
        fakeData() {
            let rows = [];
            for (let i = 1; i < 150; i++) {
                rows.push({
                    id          : i,
                    name        : 'john' + i,
                    published   : false,
                    type        : Math.round(Math.random() * 2 + 1),
                    published_at: '2017-01-02 11:12:11'
                });
            }
            return rows;
        },
        tableController() {
            let self = this;
            return new TableController({
                identifier : 'tableDemo1',
                rowsPerPage: 10,
                sortBy     : 'name',
                sortOrder  : SimpleTableSortOrder.DESCENDING,
                columns    : [
                    {
                        name    : 'id',
                        title   : 'ID',
                        align   : 'left',
                        sortable: false
                    },
                    {
                        name    : 'name',
                        title   : 'Name',
                        sortable: true,
                        optional: true,
                        align   : 'left'
                    },
                    {
                        name    : 'type',
                        title   : 'Type',
                        sortable: true,
                        optional: true,
                    },
                    {
                        name    : 'published',
                        title   : 'Published',
                        align   : 'left',
                        optional: true,
                        sortable: true,
                    },
                    {
                        name    : 'published_at',
                        title   : 'Published at',
                        align   : 'left',
                        optional: true,
                        sortable: true,
                    }
                ],
                filter     : { //AND
                    text: {
                        widget      : SimpleTableFilterTextFilter,
                        fields      : { //OR
                            name: {
                                operator: '$like'
                            },
                        },
                        hintText    : this.$t('Start typing a name'),
                        label       : this.$t('Search'),
                        visible     : true,
                        defaultValue: ''
                    },
                    type: {
                        widget           : SimpleTableFilterDropdownFilter,
                        fields           : { //OR
                            type: {
                                operator: '$eq'
                            },
                        },
                        items: async () => {
                            return [
                                {text: 'type 1', value: 1},
                                {text: 'type 2', value: 2}
                            ]
                        },
                        // items            : [
                        //     {text: 'type 1', value: 1},
                        //     {text: 'type 2', value: 2}
                        // ],
                        hintText         : this.$t('Select a type'),
                        label            : this.$t('Type'),
                        visible          : true,
                        defaultValue     : ''
                    }
                },
            }, {
                /*
                fake resource
                 */
                get(params) {

                    let filteredRows = self.fakeData;
                    filteredRows = filteredRows.filter((row) => {
                        if (params.filter && params.filter.type && params.filter.type.value) {
                            return row.type === params.filter.type.value;
                        }
                        return true;
                    });
                    let limitedRows = filteredRows.slice((params.page - 1) * params.rowsPerPage, (params.page - 1) * params.rowsPerPage + params.rowsPerPage);

                    return Promise.resolve(
                        {
                            data: {
                                rows : limitedRows,
                                count: filteredRows.length,
                                pages: Math.ceil(filteredRows.length / params.rowsPerPage),
                                page : params.page
                            }
                        }
                    )
                }
            })
        }
    },
    methods   : {
        confirmDelete() {
            return this.confirm('Are you sure to delete this item?');
        }
    }
}
