import {mapActions, mapGetters} from 'vuex';
import Events from '../../app/events';
import LocalSettings from '../../app/localSettings';
import Themes from '../../app/theme';
import Environment from '../../const/env';
import languageCodes from '../../const/languageCodes';
import LoginScreen from '../pages/login/loginScreenComponent.vue'
import events from './../../app/events';
import './../../styles/ckeditor.css';

const baitClass = 'pub300x250 pub300x250m pub728x90 text-ad textAd textad textads text-ads text-ad-links';
const baitStyle = 'width: 1000px !important; height: 1000px !important; position: absolute !important; left: -10000px !important; top: -1000px !important; display: block !important';
const attributes = ['offsetParent', 'offsetHeight', 'offsetLeft', 'offsetTop', 'offsetWidth', 'clientHeight', 'clientWidth'];

var WebFont = require('webfontloader');

const log = require('debug')('bi-admin-vue:app:single-page');

export default {
    name      : 'SinglePageApplication',
    props     : ['rootPanel'],
    components: {
        LoginScreen,
    },
    computed  : {
        activeLanguageName() {
            if (!this.availableLanguages[this.$i18n.locale]) {
                return 'unknown';
            }
            return this.availableLanguages[this.$i18n.locale].name;
        },
        languageItems() {
            let list = [];
            Object.keys(this.availableLanguages).map((langCode) => {
                list.push({
                    value: this.availableLanguages[langCode].code,
                    text : this.availableLanguages[langCode].name
                });
            });
            return list;
        },
        themes() {
            return Themes
        },
        ...mapGetters('user', {
            isLogged: 'isLogged',
            userData: 'user',
        }),
        ...mapGetters('settings', {
            settings: 'settings'
        }),
        ...mapGetters('languages', {
            availableLanguages: 'availableLanguages'
        })

    },
    data() {
        return {
            title                   : 'Admin panel',
            fontsReady              : false,
            drawerAlwaysVisible     : false,
            logoutDialogVisible     : false,
            confirmationDialog      : false,
            languageDialog          : false,
            offline                 : false,
            showAdblockWarningDialog: false,
            activeLanguage          : this.getActiveLanguageCode(),
            confirmationMessage     : '',
            notification            : {
                timeout: 5000,
                color  : 'primary',
                visible: false,
                text   : ''
            }
        }
    },
    methods   : {
        async confirmLogout() {
            let result = await this.confirm('Do you really want to logout?')
            if (result) {
                this.logoutUser();
            }
        },
        showLanguageDialog() {
            this.languageDialog = true;
        },
        switchLanguage() {
            this.$i18n.locale = this.activeLanguage;
            localStorage.setItem('locale', this.$i18n.locale);
            this.languageDialog = false;
            window.location.reload(true);
        },
        cancelLanguageDialog() {
            this.languageDialog = false;
        },
        getActiveLanguageCode() {
            log(`Active language: ${this.$i18n.locale || languageCodes.ENGLISH}`);
            return this.$i18n.locale || languageCodes.ENGLISH;
        },
        isProductionEnv() {
            return this.settings.environment === Environment.PRODUCTION;
        },
        reload() {
            location.reload();
        },
        confirmConfirmDialog() {
            this.$broadcast(events.CONFIRMATION.CLOSE, {result: true});
            this.confirmationDialog = false;
        },
        cancelConfirmDialog() {
            this.$broadcast(events.CONFIRMATION.CLOSE, {result: false});
            this.confirmationDialog = false;
        },
        ...mapActions('user', {
            getUser: 'getUser',
            logout : 'logout'
        }),
        showConfirmation(event) {
            this.confirmationMessage = event.message;
            this.confirmationDialog = true;
        },
        ...mapActions('settings', {
            getSettings: 'getSettings'
        }),
        ...mapActions('languages', {
            getLanguages: 'getLanguages'
        }),
        onUserLoggedIn() {
            this.getUser();
        },
        onFontsLoaded() {
            this.fontsReady = true;
        },
        toggleDrawer() {
            this.appManager.$emit(events.DRAWER.TOGGLE);
        },
        makeDrawerAlwaysVisible(alwaysVisible) {
            this.drawerAlwaysVisible = alwaysVisible;
        },
        closeAdblockWarningDialog() {
            this.showAdblockWarningDialog = false;
        },
        checkAttributes(attributes, bait) {
            return attributes.reduce((prev, cur) => prev || ((bait[cur] === undefined) ? false : !bait[cur]),
                false);
        },
        _insertAd() {
            const bait = window.document.createElement('div');
            bait.setAttribute('class', baitClass);
            bait.setAttribute('style', baitStyle);
            const insertionPlace = window.document.getElementsByTagName('body')[0];
            return insertionPlace.appendChild(bait);
        },
        checkAdblock() {
            let bait = this._insertAd();
            if (window.document.body.getAttribute('abp') || this.checkAttributes(attributes, bait)) {
                console.warn('adblock is running!');
                this.showAdblockWarningDialog = true;
            }
        },
        async logoutUser() {
            this.logoutDialogVisible = true;

            try {
                let result = await this.logout();
                this.showSuccessNotification('Logout was successful');
            } catch (e) {
                this.showSuccessNotification('Unable to logout');
            } finally {
                this.logoutDialogVisible = false;
            }
        },
        showNotification(type, message) {
            this.notification.color = type;
            this.notification.text = message;
            this.notification.visible = true;
            log('Error:', type, message);
        },
        showSuccessNotification(message, title) {
            this.showNotification('success', message);
        },
        showErrorNotification(message, title) {
            this.showNotification('error', message);
        },
        showWarningNotification(message, title) {
            this.showNotification('warning', message);
        }
    },
    beforeDestroy() {
        this.appManager.$off(Events.SUCCESS, this.showSuccessNotification);
        this.appManager.$off(Events.ERROR, this.showErrorNotification);
        this.appManager.$off(Events.WARNING, this.showWarningNotification);
        this.appManager.$off(Events.DRAWER.ALWAYS_VISIBLE, this.makeDrawerAlwaysVisible);
        this.appManager.$off(Events.CONFIRMATION.OPEN, this.showConfirmation);
    },
    async mounted() {
        this.appManager.$on(Events.SUCCESS, this.showSuccessNotification);
        this.appManager.$on(Events.ERROR, this.showErrorNotification);
        this.appManager.$on(Events.WARNING, this.showWarningNotification);
        this.appManager.$on(Events.DRAWER.ALWAYS_VISIBLE, this.makeDrawerAlwaysVisible);
        this.appManager.$on(Events.CONFIRMATION.OPEN, this.showConfirmation);

        WebFont.load({
            google: {
                families: ['Lato', 'Roboto', 'Material Icons']
            },
            active: this.onFontsLoaded
        });

        log('Initialising user and settings data');
        try {
            await this.getUser();
        } catch (e) {
            if (e && e.body && e.body.code !== 401) {
                log('Unable to init app. User info unavailable', e);
                this.offline = true;
                throw e;
            }
        }

        let locale = localStorage.getItem('locale');
        try {
            await this.getLanguages();
            if (locale && locale !== languageCodes.ENGLISH) {
                log(`Got locale ${locale}`);
                try {
                    let lang = await this.$http.get(`lang/${locale}`);
                    log(`Language ${locale} loaded`, lang.body);
                    if (!lang || !lang.body || !lang.body.translations) {
                        throw new Error(`No translations found in language ${locale} request`)
                    }
                    log(`Got lang ${locale} translations`, lang.body.translations);
                    this.$i18n.setLocaleMessage(locale, lang.body.translations);
                } catch (e) {
                    console.error(`Unable to load language ${locale}:`, e);
                }
            }
        } catch (e) {
            log('Unable to fetch languages. Sticking to english', e);
        }
        log('Available languages: ', this.availableLanguages);

        try {
            await this.getSettings();
            if (this.settings && this.settings.adminPanelTitle) {
                document.title = this.settings.adminPanelTitle;
                this.title = this.settings.adminPanelTitle;
            }
        } catch (e) {
            log('Unable to init app. Settings unavailable', e);
            this.offline = true;
        }
        log(`Environment: ${this.settings.environment}`);
        let theme = Themes.BLUE;
        if (this.settings.environment && this.isProductionEnv()) {
            theme = Themes.RED;
        }
        if (this.settings.environment && this.settings.environment === Environment.PREPRODUCTION) {
            theme = Themes.GREEN;
        }
        if (this.settings.environment && this.settings.environment === Environment.DEVELOPMENT) {
            theme = Themes.YELLOW;
        }
        this.$vuetify.theme = theme;
        log('Language loaded: ', this.$i18n.getLocaleMessage(locale));
        this.checkAdblock()
    },
    created() {
        this.drawerAlwaysVisible = LocalSettings.get('drawerAlwaysVisible');
    }
}
