import events from '@/app/events';
import LocalSettings from '@/app/localSettings';
import NavigationList from '@/components/partials/sidebar/NavigationList';
import {mapGetters} from 'vuex';
import Themes from '../../app/theme';
import Environments from '../../const/env';
import PermissionHandler from './../../permission/handler';
import Env from './../../util/env';

const log = require('debug')('bi:sidebar');

export default {
    name      : 'SinglePageAppRootPanel',
    components: {NavigationList},
    data() {
        return {
            width              : 250,
            drawerIsOpen       : true,
            drawerAlwaysVisible: true
        }
    },
    mounted() {
        this.appManager.$on(events.DRAWER.CLOSE, this.closeDrawer);
        this.appManager.$on(events.DRAWER.OPEN, this.openDrawer);
        this.appManager.$on(events.DRAWER.TOGGLE, this.toggleDrawer);
        this.appManager.$on(events.DRAWER.ALWAYS_VISIBLE, this.makeDrawerAlwaysVisible);
    },
    beforeDestroy() {
        this.appManager.$off(events.DRAWER.CLOSE, this.closeDrawer);
        this.appManager.$off(events.DRAWER.OPEN, this.openDrawer);
    },
    computed  : {
        ...mapGetters('settings', {
            settings: 'settings'
        }),
        ...mapGetters('user', {
            user: 'user'
        }),
        themes() {
            return Themes;
        },
        items() {
            return this.getItems();
        }
    },
    methods   : {
        isProductionEnv() {
            return this.settings.environment === Environments.PRODUCTION;
        },
        getEnvironmentName() {
            return Env.getFullName(this.settings.environment);
        },
        getItems(routes) {
            let items = [];

            if (!routes) {
                routes = this.$router.options.routes;
            }

            routes.forEach(route => {
                let item = this.getItem(route);

                let hasPermission = false;
                if ((item.permission === true || item.permission === undefined)) {
                    hasPermission = true;
                } else {
                    hasPermission = PermissionHandler.check(this.user, item.permission);
                }
                if (item && hasPermission) {
                    items.push(item);
                }
            });

            return items;
        },
        getItem(route) {

            let item = false;

            if (route.props && route.props.inMenu === true) {
                item = {
                    name      : route.props.name,
                    icon      : route.props.icon,
                    title     : this.$t(route.props.title),
                    exact     : route.props.exact,
                    permission: route.props.permission,
                }

                if (route.children) {
                    item.items = this.getItems(route.children);

                    if (
                        (route.children || !route.children.length) &&
                        (!item.items || !item.items.lenght) &&
                        !item.name
                    ) {
                        item.name = route.children[0].props.name;
                    }
                }
            }

            return item;
        },
        makeDrawerAlwaysVisible(alwaysVisible) {
            this.drawerAlwaysVisible = alwaysVisible;
        },
        isAllowed(permission) {

            return true;
        },
        closeDrawer() {
            if (this.drawerAlwaysVisible) {
                return;
            }
            this.drawerIsOpen = false;
        },
        openDrawer() {
            if (this.drawerAlwaysVisible) {
                return;
            }
            this.drawerIsOpen = true;
        },
        toggleDrawer() {
            this.drawerIsOpen = !this.drawerIsOpen;
        },
        close() {
            this.appManager.$emit(events.DRAWER.CLOSE);
        }
    },
    created() {
        this.drawerAlwaysVisible = LocalSettings.get('drawerAlwaysVisible');
    }
}
