const log = require('debug')('launcher:file-picker');

export default {
    name    : 'file-picker',
    props   : {
        assetType : {
            type   : String,
            default: 'file'
        },
        label     : {
            type   : String,
            default: 'File'
        },
        actionText: {
            type   : String,
            default: 'Select'
        },
        accept    : {
            type   : String,
            default: '*'
        },
        removable: {
            type: Boolean,
            default: true
        }
    },
    data() {
        return {
            loading : false,
            image   : null,
            progress: null
        }
    },
    methods : {
        onPickFile() {
            this.removeImage();
            this.$refs.input.click();
        },
        removeImage() {
            this.image = null;
            this.loading = false;
            this.$emit('remove');
        },
        onFilePicked(event) {
            this.loading = true;
            const files  = event.target.files || event.dataTransfer.files;

            if (files && files[0]) {
                let filename = files[0].name;

                if (filename && filename.lastIndexOf('.') <= 0) {
                    this.loading = false;
                    return;//invalid image
                }

                const fileReader = new FileReader();

                fileReader.addEventListener('load', () => {
                    this.image   = fileReader.result;
                    this.loading = false;
                    this.$emit('input', files[0]);
                });

                fileReader.readAsDataURL(files[0]);

            } else {
                this.loading = false;
            }
        },
    },
    watch   : {
        image(value) {
            this.$emit('pick', value);
        }
    },
    computed: {
        preview() {
            return this.image;
        }
    },
    created() {
        this.appManager.$on(`progress-${this.assetType}`, value => {
            this.progress = value;

            if (this.progress) {
                this.loading = true;
            } else {
                this.removeImage();
                this.loading = false;
            }
        });
    }
};
