import {mapGetters} from 'vuex';
import Editor from '../../components/editor/editor'
import ImagesResource from '../ImagesResource';
import AuthorsResource from '../AuthorsResource';
import EditorPropsDesc from './editorPropsDesc';
import SOURCE_EDITOR from './../../const/sourceEditor'

var striptags = require('striptags');

export default {
    components: {
        EditorPropsDesc,
        Editor,
    },
    computed  : {
        ...mapGetters('settings',
            {settings: 'settings'},
        ),
        sourceEditorTypes() {
            return SOURCE_EDITOR.TYPES
        }
    },
    data() {
        return {
            editorValue           : `<div><p>hello this is paragraph 1</p><p>hello this is paragraph 2</p></div>`,
            secondEditorValue     : `<p>test phrase 2</p>`,
            wordsCount            : 0,
            secondEditorWordsCount: 0,
            sourceOptions         : {
                formatAfterMount: true,
                height          : 600,
                language        : 'html'
            },
            contentOptions        : {
                uploadUrl          : '/upload/image',
                imagesBasePath     : this.settings && this.settings.imageBasePath,
                imagesListResource : ImagesResource,
                authorsListResource: AuthorsResource,
                extraplugins       : 'contextmenu',
                pluginsCustom      : [
                    'backToContents',
                    'author',
                    'picture',
                    'gallery',
                    'comparsion',
                    'youtube',
                    'facebook',
                    'tweet',
                    'twitch',
                    'vimeo',
                    'grid',
                    'instagram',
                    'soundcloud',
                    'imgur',
                    'giphy',
                    'mixer'
                ]
            },
            editorOptions         : {
                height : '300px',
                toolbar: [
                    ['Undo', 'Redo'],
                    ['Bold', 'Italic', 'Strike'],
                    ['NumberedList', 'BulletedList'],
                    ['Format', 'Link', 'Unlink'],
                    ['layout'] //TODO: add this into default toolbar!
                ],
            }
        }
    },
    methods   : {
        updateWordsCount() {
            this.wordsCount             = striptags(this.editorValue).split(' ').length;
            this.secondEditorWordsCount = striptags(this.secondEditorValue).split(' ').length;
        }
    },
    mounted() {
        this.updateWordsCount();
    },
    watch     : {
        editorValue(newVal) {
            this.updateWordsCount();
        },
        secondEditorValue() {
            this.updateWordsCount();
        }
    }
}
