import {mapActions, mapGetters} from 'vuex';

export default {
    data() {
        return {
            name      : null,
        }
    },
    props  : {
        visible: {
            type   : Boolean,
            default: false
        }
    },
    methods: {
        create() {
            this.$emit('create', this.name);
        },
        cancel() {
            this.$emit('cancel');
        }
    },
    watch  : {
        async visible(newValue) {
            if (!newValue) {
                return;
            }
            this.name = null;
        }
    }
}
